import Newsitem from './Newsitem.jsx'

export default function News() {

    const [article, setarticles] = useState("none");

    useEffect(() => {
        async function run() {
            let url = "https://newsapi.org/v2/top-headlines?country=us&apiKey=7db4862bfb1040679a3137f51055d973"
            let data = await fetch(url);
            let parseddata = await data.json();
            try {
                setarticles(parseddata);
            } catch (error) {
                console.log(error)
            }
        }
      
        run(); 
    }, []);

    console.log(article);

    return (
        <div className='contaihner my-3'>
            <h2>Top news </h2>
            <div className='row'>

                <div className='col-md-4'>
                    <Newsitem url={article.articles[0].urlToImage} title={article.articles[0].title} auth={article.articles[0].articles[0].description}/>
                </div>

            </div>
        </div>
    )
}
